import React from 'react';
import Header from '../component/Header';
import store from '../redux/store';

class Game extends React.Component {
  constructor() {
    super();

    this.state = {
      category: '',
      question: '',
      correct: '',
      incorrectAnswers: [],
    };

    this.fetchApi = this.fetchApi.bind(this);
  }

  componentDidMount() {
    this.fetchApi();
  }

  async fetchApi() {
    const { token } = store.getState().login;
    const response = await fetch(`https://opentdb.com/api.php?amount=5&token=${token}`);
    const data = await response.json();
    const correct = data.results[0].correct_answer;
    const incorrect = data.results[0].incorrect_answers;
    const { category, question } = data.results[0];
    this.setState({
      category,
      question,
      correct,
      incorrectAnswers: [...incorrect],
    });
  }

  changeColor() {
    const wrongs = document.getElementsByClassName('wrong');
    const correct = document.querySelector('.correct');
    wrongs.map((element) => element.style.border = '3px solid rgb(255, 0, 0)');
    correct.style.border = '3px solid rgb(6, 240, 15)';
  }

  render() {
    const { incorrectAnswers, correct, question, category } = this.state;
    return (
      <div>
        <Header />
        <section>
          <h4 data-testid="question-category">{ category }</h4>
          <h4 data-testid="question-text">
            { question.replace(/&quot;/gi, '"').replace(/&#039;/gi, '') }

          </h4>
          {incorrectAnswers.map((alternative, index) => (
            <button
              type="button"
              key={ alternative }
              data-testid={ `wrong-answer-${index}` }
              onClick={ this.changeColor }
              className="wrong"
            >
              {alternative}
            </button>))}
          <button
            type="button"
            key={ correct }
            data-testid="correct-answer"
            onClick={ this.changeColor }
            className="correct"
          >
            { correct }
          </button>
        </section>
      </div>
    );
  }
}

export default Game;
