import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import Header from '../component/Header';
import { setScore } from '../redux/actions';
import store from '../redux/store';

class Game extends React.Component {
  constructor() {
    super();

    this.state = {
      category: '',
      question: '',
      correct: '',
      incorrectAnswers: [],
      time: 30,
      isDisabled: false,
      questionNumber: 0,
      isButtonVisible: false,
    };

    this.fetchApi = this.fetchApi.bind(this);
    this.changeColor = this.changeColor.bind(this);
    this.timer = this.timer.bind(this);
    this.disabled = this.disabled.bind(this);
    this.score = this.score.bind(this);
  }

  componentDidMount() {
    const { saveScore } = this.props;
    saveScore({ score: 0 });
    this.fetchApi();
    this.timer();
  }

  async fetchApi() {
    const { questionNumber } = this.state;
    const { token } = store.getState().login;
    const response = await fetch(`https://opentdb.com/api.php?amount=5&token=${token}`);
    const data = await response.json();
    console.log(data.results[questionNumber]);
    const correct = data.results[questionNumber].correct_answer;
    const incorrect = data.results[questionNumber].incorrect_answers;
    const { category, question, difficulty } = data.results[questionNumber];
    this.setState({
      category,
      question,
      correct,
      difficulty,
      assertions: 0,
      incorrectAnswers: [...incorrect],
    });
  }

  changeColor() {
    const wrongs = document.querySelectorAll('.wrong');
    const correct = document.querySelector('.correct');
    wrongs.forEach((element) => { element.style.border = '3px solid rgb(255, 0, 0)'; });
    correct.style.border = '3px solid rgb(6, 240, 15)';
    const { isButtonVisible } = this.state;
    i
  }

  disabled() {
    this.setState({ isDisabled: true });
    this.changeColor();
  }

  timer() {
    const { time } = this.state;
    const mil = 1000;
    if (time !== 0) {
      setInterval(() => {
        this.setState((prevState) => {
          if (prevState.time > 0) {
            return ({ time: prevState.time - 1 });
          } this.disabled();
        });
      }, mil);
    }
  }

  score() {
    const { saveScore } = this.props;
    const base = 10;
    const { difficulty, time, assertions } = this.state;
    const actAssertion = assertions;
    this.setState({ assertions: actAssertion + 1 });
    const levels = [0, 'easy', 'medium', 'hard'];
    const { player } = store.getState().login;
    const { name, gravatarEmail } = player;
    const point = levels.indexOf(difficulty);
    const points = base + (time * point);
    const user = {
      name,
      assertions: actAssertion + 1,
      score: points,
      gravatarEmail,
    };
    localStorage.setItem('player', JSON.stringify(user));
    saveScore({ score: points, assertions: 1 });
  }

  render() {
    const { incorrectAnswers, correct,
      question, category, time, isDisabled, isButtonVisible } = this.state;
    return (
      (incorrectAnswers.length > 0) ? (
        <div>
          <Header />
          <section>
            <h4 data-testid="question-category">{ category }</h4>
            <h4 data-testid="question-text">
              { question.replace(/&quot;/gi, '"').replace(/&#039;/gi, '') }
            </h4>
            {incorrectAnswers.map((alternative, index) => (
              <button
                type="button"
                key={ alternative }
                disabled={ isDisabled }
                data-testid={ `wrong-answer-${index}` }
                onClick={ this.changeColor }
                className="wrong"
              >
                {alternative}
              </button>))}
            <button
              type="button"
              key={ correct }
              disabled={ isDisabled }
              data-testid="correct-answer"
              onClick={ () => {
                this.changeColor();
                this.score();
              } }
              className="correct"
            >
              { correct }
            </button>
            { isButtonVisible
            && <button
              data-testid="btn-next"
              type="button"
              onClick={ this.next }
            >
              Next
            </button>}
            { time }
          </section>
        </div>
      ) : <p>Loading</p>
    );
  }
}

Game.propTypes = {
  saveScore: PropTypes.func.isRequired,
};

const mapDispatchToProps = (dispatch) => ({
  saveScore: (state) => dispatch(setScore(state)),
});

export default connect(null, mapDispatchToProps)(Game);
