import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { fetchAPI } from '../redux/actions';
import store from '../redux/store';
import logo from '../trivia.png';
import '../App.css';

class Login extends React.Component {
  constructor() {
    super();

    this.state = {
    /*       name: '',
      email: '', */
      isDisable: true,
    };
    this.handleClick = this.handleClick.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange() {
    const idName = document.getElementById('input-player-name').value;
    const idEmail = document.getElementById('input-gravatar-email').value;
    this.setState({
      [idName]: idEmail,
    });
    if (idName.length > 0 && idEmail.length > 0) {
      this.setState({ isDisable: false });
    } else {
      this.setState({ isDisable: true });
    }
  }

  async handleClick() {
    const { getToken, history } = this.props;
    await getToken();
    const { token } = store.getState().login;
    localStorage.setItem('token', token);
    history.push('/game');
  }

  render() {
    const { isDisable } = this.state;
    return (
      <div className="App">
        <header className="App-header">
          <img src={ logo } className="App-logo" alt="logo" />

        <form>
          <label htmlFor="input-player-name">
            Nome
            <input
              type="text"
              name="name"
              id="input-player-name"
              data-testid="input-player-name"
              onKeyUp={ this.handleChange }
            />
          </label>
          <label htmlFor="input-gravatar-email">
            Email
            <input
              type="text"
              name="email"
              id="input-gravatar-email"
              data-testid="input-gravatar-email"
              onKeyUp={ this.handleChange }
            />
          </label>
          <button
            data-testid="btn-play"
            disabled={ isDisable }
            type="button"
            onClick={ () => this.handleClick() }
          >
            Jogar
          </button>
        </form>
        </header>
      </div>
    );
  }
}

Login.propTypes = {
  getToken: PropTypes.func.isRequired,
  history: PropTypes.shape({
    push: PropTypes.func }).isRequired,
};

const mapDispatchToProps = (dispatch) => ({
  getToken: () => dispatch(fetchAPI()),
});

export default connect(null, mapDispatchToProps)(Login);
