import { GET_TOKEN, USER_INFORMATION, USER_SCORE } from '../actions';

const INITIAL_STATE = { player: [], token: '' };

export default function login(state = INITIAL_STATE, action) {
  let newPlayer;
  switch (action.type) {
  case USER_INFORMATION:
    newPlayer = [...state.player];
    newPlayer.push({
      name: action.name,
      email: action.email,
    });
    return { ...state, player: newPlayer };
  case GET_TOKEN:
    return {
      ...state,
      token: action.data,
    };
  case USER_SCORE:
    newPlayer = [...state.player];
    return {
      ...state,
      score: action.score,
    };
  default:
    return state;
  }
}
