import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';

class Header extends React.Component {
  constructor() {
    super();

    this.state = {
      score: 0,
    };
    this.actScore = this.actScore.bind(this);
  }

  componentDidUpdate() {
    this.actScore();
  }

  actScore() {
    const newScore = localStorage.getItem('score');
    this.setState({ score: newScore });
  }

  render() {
    const { newPlayer } = this.props;
    const { score } = this.state;
    return (
      <header>
        <img
          data-testid="header-profile-picture"
          src="https://www.gravatar.com/avatar/205e460b479e2e5b48aec07710c08d50"
          alt="suggar-da-flavia"
        />
        <h3
          data-testid="header-player-name"
        >
          { newPlayer[0].name }
        </h3>
        <h3
          data-testid="header-score"
        >
          { score }
        </h3>
      </header>
    );
  }
}

Header.propTypes = {
  newPlayer: PropTypes.arrayOf(PropTypes.object).isRequired,
};

const mapStateToProps = (state) => ({
  newPlayer: state.login.newPlayer,
});

export default connect(mapStateToProps)(Header);
