const INITIAL_STATE = { player: [] };
const SUBMITION = 'SUBMITION';

export default function login(state = INITIAL_STATE, action) {
  let newPlayer;
  switch (action.type) {
  case SUBMITION:
    newPlayer = [...state.player];
    newPlayer.push({
      name: action.name,
      email: action.email,
    });

    return { ...state, newPlayer };
  default:
    return state;
  }
}
